import React, { Component } from 'react';

import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';

const styles = () => ({
  wrapper: {
    display: 'flex',
    justifyContent: 'flex-start',
    alignItems: 'center',
    padding: '10px 0px 10px 15px',
    margin: 0,
    maxWidth: '100%'
  },
  avatar: {
    display: 'flex',
    maxWidth: 50,
    maxHeight: 50,
    borderRadius: '25%',
    marginRight: 12
  },
  avatarSelected: {
    display: 'flex',
    maxWidth: 50,
    maxHeight: 50,
    borderRadius: '25%',
    marginRight: 12,
    boxShadow: '4px 4px 5px #aaa'
  },
  username: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textTransform: 'capitalize',
    color: 'black'
  },
  usernameSelected: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'red',
    textShadow: '3px 3px 5px #aaa',
    textTransform: 'capitalize',
    fontSize: '1.3em'
  }
});

class User extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: null
    };
  }

  handleReceiverChange = () => {
    this.props.onReceiverChange(this.props.user);
  };

  render() {
    const { classes, user, selected } = this.props;
    const tempAvatar =
      'data:image/png;base64,';

    return (
      <Grid
        container
        onClick={this.handleReceiverChange}
        className={classes.wrapper}
        spacing={16}
      >
        <img
          className={selected ? classes.avatarSelected : classes.avatar}
          src={tempAvatar}
          alt="Avatar"
        />
        <span
          className={selected ? classes.usernameSelected : classes.username}
        >
          {user.name}
        </span>
      </Grid>
    );
  }
}

export default withStyles(styles)(User);
